INSERT INTO `org_info2` (`RowID`, `OrgID`, `Status`, `UserID`, `Email`, `Password`, `OrgName`, `CityID`, `Address`, `Contact`, `CellPhone`, `DeskPhone`, `Business`, `OrgProfile`, `CreatTime`, `LastEditTime`) VALUES (1, '200010000008', 'p', '10000024', '1234abcd@qq.com', '1234abcd', '飞扬成长儿童教育', '020', '天河区冼村路56号高德大厦12楼405', '高小军', '13312341234', '02085341234', '学前教育、奥数培训、亲子旅游、科学教育', '\r\n我们是儿童教育领域的专家\r\n\r\n　　致力于帮助0至8岁的孩子进行体能、情感、认知及社交能力的发展，目前在全世界31个国家和地区拥有300多个教育中心。美吉姆独特的课程综合了精彩趣味的游戏、运动、体操、音乐、舞蹈、接力比赛、骑乘游戏等，在每周一次的结构性的适龄性课程中，孩子们在获得无限欢乐的同时收获力量、平衡、协调、敏捷和灵活性的发展，进而发展社交能力，并树立良好的自信心和自尊心。美吉姆先进的教学设备、训练有素的教师、众多获奖的课程、合理的学生与老师配比都为美吉姆在儿童早期教育领域赢得了极高的声誉。\r\n \r\n我们通过有趣的学习帮助儿童拥有健康\r\n\r\n　　我们的教学设施、课程和服务帮助孩子获得成为一个优秀的年轻个体所必备的各种技能，并通过营造充满理解、积极鼓励、持续支持和非竞争的氛围，使学习变得更加有趣和充满欢乐，帮助孩子获取自信和正面的自我形象。\r\n \r\n我们致力于改善全世界儿童的生活\r\n\r\n　　在体能锻炼的过程中孩子所体会到的喜悦和成就感，会帮助孩子建立良好的自尊和自信，并在之后的生活中影响到孩子发展的各个方面。在和训练有素的美吉姆老师互动的过程中，孩子们学习到如何与充满爱心的成人间建立温暖关系，从而更容易适应之后的幼儿园或学前班生活。在美吉姆，孩子更好的了解自己，学习到独立、自律、社交能力，甚至会幽默感， 并进一步增强自我认同感。对自己感觉良好的孩子会在通往成功的道路上一马当先。\r\n', '2016-3-24 23:28:22', '2016-3-24 23:28:22');
