  `CourseTypeID` char(2) DEFAULT NULL COMMENT '课程类型代码',
  `Subject` varchar(200) DEFAULT NULL COMMENT '主题名称',
  `StartDate` datetime DEFAULT NULL COMMENT '课程开始时间',
  `EndDate` datetime DEFAULT NULL COMMENT '课程结束时间',
  `DateNote` varchar(50) DEFAULT NULL COMMENT '课程时间说明',
  `StudentType` char(1) DEFAULT NULL COMMENT '学生类别。\r\n            0 - 成人\r\n            1 - 儿童\r\n            2 -成人或儿童\r\n            ',
  `CityID` char(4) DEFAULT NULL COMMENT '课程城市代码',
  `Cit_CityID` char(4) DEFAULT NULL COMMENT '城市代码',
  `Address` varchar(200) DEFAULT NULL COMMENT '地点',
  `Price` double DEFAULT NULL COMMENT '费用',
  `Duration` double DEFAULT NULL COMMENT '课程/咨询时长',
  `DurationUnitID` char(1) DEFAULT NULL COMMENT '时长单位编码。\r\n            0 - 次\r\n            1 - 课时\r\n            2 - 小时\r\n            3 - 天\r\n            ',
  `DurationNote` varchar(30) DEFAULT NULL COMMENT '课时说明。例如“3小时起”，“共10课时”“1课时45分钟”等',
  `MaxAttendees` int(11) DEFAULT NULL COMMENT '人数上限',
  `KeyWords` varchar(80) DEFAULT NULL COMMENT '关键字',
