  `CourseCont` text COMMENT '内容介绍',
  `TeachMethod` varchar(30) DEFAULT NULL COMMENT '教学方式。\r\n            主题咨询：面对面/ 电话/视频\r\n            私教：上门/固定地点/面议\r\n            ',
  `TeachAreaNote` varchar(80) DEFAULT NULL COMMENT '地点说明',
